      {/* 戻るリンク */}
      <div className="mt-8 text-center">
        <Link
          to="/reports"
          className="text-indigo-600 hover:text-indigo-500"
        >
          ← 月報一覧に戻る
        </Link>
      </div>
    </div>
  );
};

export default ReportDetail;
