import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../services/api';
import { toast } from '../utils/toast';

interface ConversationSession {
  session_id: string;
  question: string | null;
  question_type: string;
  category: string;
  progress: number;
  total_questions: number;
  session_data: any;
  is_complete: boolean;
}

const ConversationalReport: React.FC = () => {
  const navigate = useNavigate();
  const [session, setSession] = useState<ConversationSession | null>(null);
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [interimText, setInterimText] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [recognition, setRecognition] = useState<any>(null);
  const [isListening, setIsListening] = useState(false);
  const [lastResultIndex, setLastResultIndex] = useState(0);

  // Èü≥Â£∞Ë™çË≠ò„ÅÆÂàùÊúüÂåñ
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
      const recognitionInstance = new SpeechRecognition();

      recognitionInstance.continuous = true;
      recognitionInstance.interimResults = true;
      recognitionInstance.lang = 'ja-JP';
      recognitionInstance.maxAlternatives = 1;

      recognitionInstance.onresult = (event: any) => {
        let finalTranscript = '';
        let interimTranscript = '';

        // lastResultIndex‰ª•Èôç„ÅÆÊñ∞„Åó„ÅÑÁµêÊûú„ÅÆ„Åø„ÇíÂá¶ÁêÜ
        for (let i = lastResultIndex; i < event.results.length; i++) {
          const result = event.results[i];
          if (result.isFinal) {
            finalTranscript += result[0].transcript;
            setLastResultIndex(i + 1); // Âá¶ÁêÜÊ∏à„Åø„Ç§„É≥„Éá„ÉÉ„ÇØ„Çπ„ÇíÊõ¥Êñ∞
          } else {
            interimTranscript += result[0].transcript;
          }
        }

        // Á¢∫ÂÆö„Åó„Åü„ÉÜ„Ç≠„Çπ„Éà„Åå„ÅÇ„ÇãÂ†¥Âêà„ÅÆ„ÅøËøΩÂä†
        if (finalTranscript.trim()) {
          setCurrentAnswer(prev => prev + finalTranscript);
          console.log('Á¢∫ÂÆö„ÉÜ„Ç≠„Çπ„ÉàËøΩÂä†:', finalTranscript);
        }

        // „É™„Ç¢„É´„Çø„Ç§„É†„ÉÜ„Ç≠„Çπ„Éà„ÇíÊõ¥Êñ∞
        setInterimText(interimTranscript);
      };

      recognitionInstance.onstart = () => {
        console.log('Èü≥Â£∞Ë™çË≠ò„ÅåÈñãÂßã„Åï„Çå„Åæ„Åó„Åü');
        setLastResultIndex(0); // ÈñãÂßãÊôÇ„Å´„Ç§„É≥„Éá„ÉÉ„ÇØ„Çπ„Çí„É™„Çª„ÉÉ„Éà
        setInterimText('');
      };

      recognitionInstance.onend = () => {
        console.log('Èü≥Â£∞Ë™çË≠ò„ÅåÁµÇ‰∫Ü„Åó„Åæ„Åó„Åü');
        setInterimText(''); // interim „ÉÜ„Ç≠„Çπ„Éà„Çí„ÇØ„É™„Ç¢
        if (isListening) {
          // „Åæ„Å†Èå≤Èü≥‰∏≠„ÅÆÂ†¥Âêà„ÅØËá™ÂãïÁöÑ„Å´ÂÜçÈñã
          setTimeout(() => {
            if (isListening) {
              try {
                recognitionInstance.start();
              } catch (error) {
                console.error('Èü≥Â£∞Ë™çË≠ò„ÅÆÂÜçÈñã„Å´Â§±Êïó:', error);
                setIsListening(false);
                setIsRecording(false);
              }
            }
          }, 100);
        } else {
          setIsListening(false);
          setIsRecording(false);
        }
      };

      recognitionInstance.onerror = (event: any) => {
        console.error('Èü≥Â£∞Ë™çË≠ò„Ç®„É©„Éº:', event.error);
        if (event.error === 'no-speech' || event.error === 'audio-capture') {
          // Èü≥Â£∞„ÅåÊ§úÂá∫„Åï„Çå„Å™„ÅÑÂ†¥Âêà„ÅØËá™ÂãïÁöÑ„Å´ÂÜçÈñã„ÇíË©¶„Åø„Çã
          if (isListening) {
            setTimeout(() => {
              if (isListening) {
                try {
                  recognitionInstance.start();
                } catch (error) {
                  console.error('Èü≥Â£∞Ë™çË≠ò„ÅÆÂÜçÈñã„Å´Â§±Êïó:', error);
                  setIsListening(false);
                  setIsRecording(false);
                }
              }
            }, 100);
          }
        } else {
          setIsListening(false);
          setIsRecording(false);
          toast.error(`Èü≥Â£∞Ë™çË≠ò„Ç®„É©„Éº: ${event.error}`);
        }
      };

      setRecognition(recognitionInstance);
    }
  }, [isListening]);

  // ÂØæË©±„Çª„ÉÉ„Ç∑„Éß„É≥„ÅÆÈñãÂßã
  const startConversation = async () => {
    setIsLoading(true);
    try {
      const response = await api.post('/conversation/start');
      setSession(response.data);
      toast.success('ÂØæË©±ÂûãÊúàÂ†±‰ΩúÊàê„ÇíÈñãÂßã„Åó„Åæ„Åó„Åü');
    } catch (error) {
      console.error('„Çª„ÉÉ„Ç∑„Éß„É≥ÈñãÂßã„Ç®„É©„Éº:', error);
      toast.error('„Çª„ÉÉ„Ç∑„Éß„É≥„ÅÆÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setIsLoading(false);
    }
  };

  // Èü≥Â£∞Ë™çË≠ò„ÅÆÈñãÂßã/ÂÅúÊ≠¢
  const toggleRecording = () => {
    if (!recognition) {
      toast.error('Èü≥Â£∞Ë™çË≠ò„ÅØ„Çµ„Éù„Éº„Éà„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì');
      return;
    }

    if (isListening) {
      console.log('Èü≥Â£∞Ë™çË≠ò„ÇíÂÅúÊ≠¢„Åó„Åæ„Åô');
      recognition.stop();
      setIsListening(false);
      setIsRecording(false);
      setInterimText('');
      setLastResultIndex(0);
    } else {
      console.log('Èü≥Â£∞Ë™çË≠ò„ÇíÈñãÂßã„Åó„Åæ„Åô');
      try {
        setIsListening(true);
        setIsRecording(true);
        setLastResultIndex(0);
        setInterimText('');
        recognition.start();
        toast.info('Èü≥Â£∞Ë™çË≠ò„ÇíÈñãÂßã„Åó„Åæ„Åó„Åü„ÄÇË©±„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
      } catch (error) {
        console.error('Èü≥Â£∞Ë™çË≠ò„ÅÆÈñãÂßã„Å´Â§±Êïó:', error);
        setIsListening(false);
        setIsRecording(false);
        setInterimText('');
        setLastResultIndex(0);
        toast.error('Èü≥Â£∞Ë™çË≠ò„ÅÆÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
      }
    }
  };

  // ÂõûÁ≠î„ÅÆÈÄÅ‰ø°
  const submitAnswer = async () => {
    if (!currentAnswer.trim() || !session) return;

    setIsLoading(true);
    try {
      const response = await api.post('/conversation/answer', {
        session_id: session.session_id,
        answer: currentAnswer.trim(),
        session_data: session.session_data
      });

      const newSession = response.data;
      setSession(newSession);
      setCurrentAnswer('');

      if (newSession.is_complete) {
        // ÂØæË©±ÂÆå‰∫Ü„ÄÅÊúàÂ†±ÁîüÊàê
        await generateReport(newSession.session_data);
      }
    } catch (error) {
      console.error('ÂõûÁ≠îÈÄÅ‰ø°„Ç®„É©„Éº:', error);
      toast.error('ÂõûÁ≠î„ÅÆÈÄÅ‰ø°„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setIsLoading(false);
    }
  };

  // ÊúàÂ†±„ÅÆÁîüÊàê
  const generateReport = async (sessionData: any) => {
    try {
      const response = await api.post('/conversation/generate-report', sessionData);
      toast.success('ÊúàÂ†±„ÅåÊ≠£Â∏∏„Å´ÁîüÊàê„Åï„Çå„Åæ„Åó„ÅüÔºÅ');
      navigate(`/reports/${response.data.report_id}`);
    } catch (error) {
      console.error('ÊúàÂ†±ÁîüÊàê„Ç®„É©„Éº:', error);
      toast.error('ÊúàÂ†±„ÅÆÁîüÊàê„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    }
  };

  // ÈÄ≤ÊçóÁéá„ÅÆË®àÁÆó
  const progressPercentage = session ? Math.round((session.progress / session.total_questions) * 100) : 0;

  // „Ç´„ÉÜ„Ç¥„É™„Éº„ÅÆÊó•Êú¨Ë™ûÂêç
  const getCategoryName = (category: string) => {
    const categoryNames: { [key: string]: string } = {
      'basic_info': 'Âü∫Êú¨ÊÉÖÂ†±',
      'work_time': 'Á®ºÂÉçÊôÇÈñì',
      'sales_activities': 'Âñ∂Ê•≠Ê¥ªÂãï',
      'financial': 'ÂèéÂÖ•',
      'reflection': 'ÊåØ„ÇäËøî„Çä',
      'completed': 'ÂÆå‰∫Ü'
    };
    return categoryNames[category] || category;
  };

  if (!session) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full">
          <div className="text-center">
            <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-6">
              <svg className="w-10 h-10 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
              </svg>
            </div>
            <h1 className="text-3xl font-bold text-gray-900 mb-4">
              üé§ ÂØæË©±ÂûãÊúàÂ†±‰ΩúÊàê
            </h1>
            <p className="text-gray-600 mb-8">
              AI„ÅåË≥™Âïè„Çí„Åó„Åæ„Åô„ÅÆ„Åß„ÄÅÈü≥Â£∞„Åæ„Åü„ÅØÊñáÂ≠ó„ÅßÁ≠î„Åà„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ<br />
              Ëá™ÁÑ∂„Å´‰ºöË©±„Åô„Çã„Å†„Åë„Åß„ÄÅÊúàÂ†±„ÅåËá™ÂãïÁöÑ„Å´‰ΩúÊàê„Åï„Çå„Åæ„Åô„ÄÇ
            </p>
            <button
              onClick={startConversation}
              disabled={isLoading}
              className="bg-blue-600 text-white px-8 py-3 rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors font-semibold"
            >
              {isLoading ? 'Ê∫ñÂÇô‰∏≠...' : 'üé§ ÂØæË©±„ÇíÈñãÂßã„Åô„Çã'}
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (session.is_complete) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-100 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full text-center">
          <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <svg className="w-10 h-10 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-4">ÂØæË©±ÂÆå‰∫ÜÔºÅ</h2>
          <p className="text-gray-600 mb-6">
            „Åô„Åπ„Å¶„ÅÆË≥™Âïè„Å´„ÅäÁ≠î„Åà„ÅÑ„Åü„Å†„Åç„ÄÅ„ÅÇ„Çä„Åå„Å®„ÅÜ„Åî„Åñ„ÅÑ„Åæ„Åó„Åü„ÄÇ<br />
            ÊúàÂ†±„ÇíÁîüÊàê„Åó„Å¶„ÅÑ„Åæ„Åô...
          </p>
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600 mx-auto"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-100 p-4">
      <div className="max-w-4xl mx-auto">
        {/* „Éò„ÉÉ„ÉÄ„Éº */}
        <div className="bg-white rounded-t-2xl shadow-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-2xl font-bold text-gray-900">ÂØæË©±ÂûãÊúàÂ†±‰ΩúÊàê</h1>
            <span className="bg-purple-100 text-purple-800 px-3 py-1 rounded-full text-sm font-medium">
              {getCategoryName(session.category)}
            </span>
          </div>

          {/* ÈÄ≤Êçó„Éê„Éº */}
          <div className="mb-4">
            <div className="flex justify-between text-sm text-gray-600 mb-2">
              <span>ÈÄ≤ÊçóÁä∂Ê≥Å</span>
              <span>{session.progress} / {session.total_questions}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-purple-600 h-2 rounded-full transition-all duration-300"
                style={{ width: `${progressPercentage}%` }}
              ></div>
            </div>
            <div className="text-center text-sm text-gray-500 mt-1">
              {progressPercentage}% ÂÆå‰∫Ü
            </div>
          </div>
        </div>

        {/* Ë≥™Âïè„Ç®„É™„Ç¢ */}
        <div className="bg-white shadow-lg p-8">
          <div className="mb-8">
            <div className="flex items-start space-x-4">
              <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center flex-shrink-0">
                <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8.228 9c.549-1.165 2.03-2 3.772-2 2.21 0 4 1.343 4 3 0 1.4-1.278 2.575-3.006 2.907-.542.104-.994.54-.994 1.093m0 3h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div className="flex-1">
                <h3 className="text-lg font-semibold text-gray-900 mb-2">Ë≥™Âïè</h3>
                <p className="text-gray-700 text-lg leading-relaxed">{session.question}</p>
              </div>
            </div>
          </div>

          {/* ÂõûÁ≠î„Ç®„É™„Ç¢ */}
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                „ÅÇ„Å™„Åü„ÅÆÂõûÁ≠î
              </label>
              <textarea
                value={currentAnswer}
                onChange={(e) => setCurrentAnswer(e.target.value)}
                placeholder="„Åì„Åì„Å´ÂõûÁ≠î„ÇíÂÖ•Âäõ„Åô„Çã„Åã„ÄÅÈü≥Â£∞ÂÖ•Âäõ„Éú„Çø„É≥„Çí‰ΩøÁî®„Åó„Å¶„Åè„Å†„Åï„ÅÑ..."
